import React, { useState } from 'react'
import './../CSS/SignUp.css'
import _ from 'lodash'


const SignUp=() =>{

    const[name, setname] = useState('')
    const [username, setUsername] = useState('')
    const [email, setemail] = useState('')
    const [pn, setpn] = useState('')
    const [password, setpassword] = useState('')
    const [push, setpush] = useState(true)


    const getCredentials = () => JSON.parse(localStorage.getItem('credentials') || "[]")
    const getDetails = () => JSON.parse(localStorage.getItem('details') || "[]")

    const signUp = ()=>{
        var credentials = getCredentials(),
        details = getDetails(),
        detail = {
            name: name, 
            username: username,
            email: email,
            phoneNumber: pn,
            password: password
        }

        push = true

        for(let i in details){
            if (_.isEqual(details[i], detail)){
                push = false
                break
            }
        }
        if (push)
        {
            var credential = {
                username: username,
                password: password
            }

            credentials.push(credential)
            details.push(detail)

            localStorage.setItem('details', JSON.stringify(details))
            localStorage.setItem('credentials', JSON.stringify(credentials))
        }
    }

    return(
        <div>
            <div className='signUpInfo'>
                New to facebook? SignUp here.
                <br />
                It's free and will always be.
            </div>
            <ul className='signUp'>
                <li>
                    <input type="text" placeholder='Name' onChange={(e)=>{setname(e.target.value)}} />
                </li>
                <li>
                    <input type="email" placeholder='Email' onChange={(e)=>{setemail(e.target.value)}} />
                </li>
                <li>
                    <input type="text" placeholder='Phone number' onChange={(e)=>{setpn(e.target.value)}} />
                </li>
                <li>
                    <input type="text" placeholder='Username' onChange={(e)=>{setUsername(e.target.value)}} />
                </li>
                <li>
                    <input type="password" placeholder='passwrod' onChange={(e)=>{setpassword(e.target.value)}} />
                </li>
                <li>
                    <button className="btn btn-secondary" onClick={signUp} >Sign Up</button>
                </li>
            </ul>
        </div>
    )
}

export default SignUp;
