import React, { useState } from 'react'
import { useLocation, useParams } from 'react-router';
import {Link} from 'react-router-dom'

function LoggedIn (){

    const {validz, search} = useLocation(); //pass prop using link from navbar.js, access by using 'validz'

    const getCredentials = ()=> JSON.parse(localStorage.getItem('credentials') || "[]");
    const getDetails = () => JSON.parse(localStorage.getItem('details') || "[]")
    const getInput = () => localStorage.getItem('userinput')
    const isValid = () => localStorage.getItem('valid')

    const[valid, setvalid] = useState(isValid())
    const[id, setid] = useState(localStorage.getItem('id'))

    return(
        <div>
            {
                (
                    ()=>{
                        if(valid==='true'){
                            return (
                                <div>
                                    Login Successful !
                                </div>
                            )
                        }
                        else{
                            return (
                                <div>
                                    <h2>Login Failed, Please check your credentials and try again</h2>
                                </div>
                            )
                        }
                    }
                )()
            }
            <Link to='/'>
                <p>Back</p>
            </Link>
        </div>
    )

}

export default LoggedIn;
